import{_ as p,c as e,o as l,d as i}from"./app.a0f52c8e.js";const v='{"title":"\u4E8B\u4EF6\u5FAA\u73AF EventLoop","description":"","frontmatter":{},"headers":[{"level":3,"title":"\u7EBF\u7A0B","slug":"\u7EBF\u7A0B"},{"level":3,"title":"\u4EFB\u52A1","slug":"\u4EFB\u52A1"}],"relativePath":"guide/FrontEnd/JavaScript/EventLoop.md"}',a={},t=i('<h1 id="\u4E8B\u4EF6\u5FAA\u73AF-eventloop" tabindex="-1">\u4E8B\u4EF6\u5FAA\u73AF EventLoop <a class="header-anchor" href="#\u4E8B\u4EF6\u5FAA\u73AF-eventloop" aria-hidden="true">#</a></h1><p>\u5728\u4E86\u89E3Js\u4E8B\u4EF6\u5FAA\u73AF\u673A\u5236\u4E4B\u524D\u6211\u4EEC\u8981\u4E86\u89E3\u4E00\u4E0B\u51E0\u4E2A\u7279\u6027\uFF1A</p><ul><li>Js \u662F\u5355\u7EBF\u7A0B\u6267\u884C\u73AF\u5883\u3002</li><li>Js \u4EE3\u7801\u8FD0\u884C\u65F6\u5F02\u6B65\u7684\u3002</li></ul><h3 id="\u7EBF\u7A0B" tabindex="-1">\u7EBF\u7A0B <a class="header-anchor" href="#\u7EBF\u7A0B" aria-hidden="true">#</a></h3><p>1.\u4EC0\u4E48\u662F\u7EBF\u7A0B\uFF1F</p><p>\u7EBF\u7A0B\u662F\u7A0B\u5E8F\u6267\u884C\u7684\u6700\u5C0F\u5355\u4F4D\uFF0C\u8FDB\u7A0B\u6267\u884C\u7684\u57FA\u672C\u5355\u4F4D\u3002</p><p>\u7B80\u5355\u7684\u8BF4\u8FDB\u7A0B\u5C31\u662F\u4E00\u4E2A\u5E94\u7528\u7A0B\u5E8F\uFF0C\u800C\u7EBF\u7A0B\u5C31\u662F\u7A0B\u5E8F\u8FD0\u884C\u4E2D\u4EFB\u52A1\u7684\u771F\u662F\u6267\u884C\u8005\u3002</p><p>\u901A\u5E38\u4E00\u4E2A\u8FDB\u7A0B\u6709\u591A\u4E2A\u7EBF\u7A0B\uFF0C\u591A\u4E2A\u7EBF\u7A0B\u5171\u4EAB\u8FDB\u7A0B\u5F53\u4E2D\u7684\u8D44\u6E90\uFF0C\u5E76\u4E14\u6BCF\u4E2A\u7EBF\u7A0B\u662F\u76F8\u4E92\u72EC\u7ACB<code>\u5E76\u884C</code>\u6267\u884C\u7684\uFF0C\u6BCF\u4E2A\u7EBF\u7A0B\u6709\u5404\u81EA\u7684\u5BC4\u5B58\u5668\uFF08\uFF09\u73AF\u5883\uFF0C\u5404\u81EA\u7684\u8C03\u7528\u6808\uFF08call task\uFF09\u4EE5\u53CA\u5404\u81EA\u7684\u672C\u5730\u5B58\u50A8\u3002</p><p>2.\u5355\u7EBF\u7A0B\u6709\u4EC0\u4E48\u597D\u5904\uFF1F</p><p>\u5355\u7EBF\u7A0B\u5C31\u610F\u5473\u7740\uFF0C\u540C\u4E00\u65F6\u95F4\u53EA\u80FD\u6267\u884C\u4E00\u4E2A\u4EFB\u52A1\u3002</p><p>\u6240\u6709\u7684\u4EFB\u52A1\u90FD\u8981\u6392\u961F\uFF0C\u524D\u9762\u7684\u4EFB\u52A1\u5B8C\u6210\u540E\u624D\u80FD\u6267\u884C\u540E\u9762\u7684\u4EFB\u52A1\u3002</p><p>\u5982\u679C\u524D\u9762\u7684\u4EFB\u52A1\u65F6\u95F4\u5F88\u957F\uFF0C\u5C31\u4F1A\u5BFC\u81F4\u961F\u5217\u963B\u585E\uFF0Ccpu\u7A7A\u95F2\u3002\u6BD4\u5982I/O\u64CD\u4F5C\u8017\u65F6\u4F46\u662Fcpu\u95F2\u7F6E\uFF0C\u9020\u6210\u4E86\u6027\u80FD\u6D6A\u8D39\u3002</p><p>\u597D\u5904\uFF1A--\u7B80\u5355</p><p>\u5355\u7EBF\u7A0B\u7684\u597D\u5904\u5C31\u662F\uFF0C\u7B80\u5355\uFF0C\u5982\u679C\u662F\u591A\u7EBF\u7A0B\u7684\u8BDD\uFF0C\u540C\u4E00\u65F6\u95F4\u4E00\u4E2A\u53BF\u57CE\u8981\u63D2\u5165div \uFF0C\u53E6\u4E00\u4E2A\u7EBF\u7A0B\u6267\u884C\u5220\u9664div\u64CD\u4F5C\uFF0C\u8FD9\u4E2A\u65F6\u95F4\u5C31\u4F1A\u51FA\u73B0\u5F88\u591A\u95EE\u9898\uFF0C\u6211\u4EEC\u8BDD\u9700\u8981\u4E3A\u6B64\u53EA\u80FD\u52A0\u9501\u673A\u673A\u5236\u7B49\u7B49\u3002Js\u6700\u521D\u8BBE\u8BA1\u51FA\u6765\u662F\u4E3A\u4E86\u505A\u8868\u5355\u9A8C\u8BC1\u4EE5\u53CADOM\u64CD\u4F5C\u7684\uFF0C\u5F88\u95F2\u7136\u4E0D\u9700\u8981\u8FD9\u4E48\u590D\u6742\u7684\u64CD\u4F5C\u3002</p><p>\u7F3A\u70B9\uFF1A--\u6027\u80FD\u6D6A\u8D39</p><p>\u4E3A\u4E86\u89E3\u51B3\u5355\u7EBF\u7A0B\u961F\u5217\u963B\u585E\u3001\u6D6A\u8D39\u8D44\u6E90\u7684\u95EE\u9898\uFF0C\u6211\u4EEC\u4F7F\u7528\u989D\u5F02\u6B65\u7684\u65B9\u6CD5\u6267\u884C\u4EFB\u52A1\u3002</p><p>JS\u591A\u7EBF\u7A0B\uFF1A</p><p>\u5728HTML5\u4E2D\uFF0C\u5F15\u5165\u4E86Web Worker\u8FD9\u4E2A\u6982\u5FF5\u3002\u5B83\u80FD\u591F\u5728\u53E6\u5916\u4E00\u4E2A\u7EBF\u7A0B\u4E2D\u6267\u884C\u8BA1\u7B97\u5BC6\u96C6\u7684js\u4EE3\u7801\u800C\u4E0D\u5F15\u8D77\u9875\u9762\u5361\u6B7B\uFF0C\u8FD9\u662F\u771F\u6B63\u7684\u591A\u7EBF\u7A0B\u3002\u4E3A\u4E86\u4FDD\u8BC1\u7EBF\u7A0B\u7684\u5B89\u5168\uFF0CWorker \u4E2D\u7684\u4EE3\u7801\u4E0D\u80FD\u8BBF\u95EEDOM\u3002</p><p>3.\u4EC0\u4E48\u662F\u5F02\u6B65\uFF1F</p><p>\u5F02\u6B65\u7684\u6982\u5FF5\u662F\u76F8\u5BF9\u4E8E\u540C\u6B65\u7684\u6982\u5FF5\u6765\u8BF4\u7684\u3002</p><p>\u540C\u6B65\uFF1A\u540C\u6B65\u5C31\u662F\u4EFB\u52A1\u6309\u7167\u987A\u5E8F\u4F9D\u6B21\u6267\u884C\u3002\u53EA\u6709\u4E0A\u4E00\u4E2A\u4EFB\u52A1\u6267\u884C\u5B8C\u6210\u4EE5\u540E\u624D\u80FD\u6267\u884C\u4E0B\u4E00\u4E2A\u4EFB\u52A1\u3002</p><p>\u5F02\u6B65\uFF1A\u5F02\u6B65\u4F1A\u628A\u5F02\u6B65\u4EFB\u52A1\u9AA8\u6C14\uFF0C\u7EE7\u7EED\u6267\u884C\u540E\u9762\u7684\u540C\u6B65\u4EFB\u52A1\u3002\u7B49\u5230\u6240\u6709\u7684\u540C\u6B65\u4EFB\u52A1\u90FD\u6267\u884C\u5B8C\u6210\u4EE5\u540E\u624D\u4F1A\u6267\u884C\u5F02\u6B65\u4EFB\u52A1\u3002</p><p>\u5F02\u6B65\u4EFB\u52A1\u7684\u5B9E\u73B0\u4F9D\u8D56\u4E8EJs\u5F15\u64CE\u7684\u65F6\u95F4\u5FAA\u73AF\u673A\u5236\u3002</p><p><strong>\u4E8B\u4EF6\u5FAA\u73AF</strong>\uFF1A</p><ol><li><p>javascript \u5185\u5B58\u673A\u5236</p><ul><li>\u8C03\u7528\u6808 \uFF08call stack\uFF09\uFF1A\u7528\u4E8E\u4E3B\u7EBF\u7A0B\u4EFB\u52A1\u6267\u884C\u3002</li><li>\u5806 \uFF08Heap\uFF09\uFF1A\u5B58\u653E\u7ED3\u6784\u5316\u6570\u636E\uFF0C \u53D8\u91CF\u3001\u5BF9\u8C61\u7B49\u7B49\u3002</li><li>\u4EFB\u52A1\u961F\u5217\uFF08Queque\uFF09\uFF1A \u5B58\u653E\u5F02\u6B65\u4EFB\u52A1\u4E0E\u5B9A\u65F6\u4EFB\u52A1\uFF08\u5B9E\u9645\u5B58\u653E\u7684\u662F\u5F02\u6B65\u4EFB\u52A1\u7684\u56DE\u8C03\u51FD\u6570\uFF09\u3002</li></ul></li><li><p>\u4EE3\u7801\u6267\u884C\u673A\u5236</p><ul><li>\u6240\u6709\u4EFB\u52A1\u90FD\u5728\u4E3B\u7EBF\u7A0B\u4E0A\u9762\u6267\u884C\u3002</li><li>\u4E3B\u7EBF\u7A0B\u4E4B\u5916\u8FD8\u5B58\u5728\u4E00\u4E2A <strong>\u4EFB\u52A1\u961F\u5217</strong>\u3002\u53EA\u8981\u5F02\u6B65\u4EFB\u52A1\u6267\u884C\u6709\u4E86\u7ED3\u679C\u5C31\u5728\u4EFB\u52A1\u961F\u5217\u5F53\u4E2D\u653E\u7F6E\u4E00\u4E2A\u4E8B\u4EF6\u3002</li><li>\u4E00\u65E6\u6808\u4E2D\u7684\u6240\u6709\u540C\u6B65\u4EFB\u52A1\u6267\u884C\u5B8C\u6BD5\uFF0C\u5C31\u5BF9\u8BFB\u53D6\u4EFB\u52A1\u961F\u5217\uFF0C\u9009\u62E9\u9996\u5148\u6267\u884C\u7684\u4EFB\u52A1\uFF08\u7531\u6D4F\u89C8\u5668\u51B3\u5B9A\uFF09\uFF0C\u77E5\u9053\u4EFB\u52A1\u961F\u5217\u6CA1\u6709\u53EF\u6267\u884C\u7684\u4EFB\u52A1\u3002</li></ul></li><li><p>Event Loop</p><p>\u4E8B\u4EF6\u5FAA\u73AF\uFF0C\u5C31\u662F\u4E3B\u7EBF\u7A0B\u4E0D\u65AD\u8BFB\u53D6\u4EFB\u52A1\u961F\u5217\uFF0C\u5C06\u53EF\u4EE5\u53EF\u6267\u884C\u7684\u4EFB\u52A1\uFF0C\u6309\u7167\u987A\u5E8F\uFF08\u987A\u5E8F\u7531\u6D4F\u89C8\u5668\u51B3\u5B9A\uFF09\u653E\u5165\u6267\u884C\u6808\u5F53\u4E2D\u6267\u884C\u3002</p></li><li><p>\u5E38\u89C1\u7684\u5F02\u6B65\u4EFB\u52A1</p><ul><li>DOM \u64CD\u4F5C</li><li>\u7F51\u7EDC\u64CD\u4F5C\uFF08ajax\uFF09\u8BF7\u6C42</li><li>\u5B9A\u65F6\u5668</li></ul><p>\u65F6\u95F4\u5FAA\u73AF\u673A\u5236\u56FE\u89E3\uFF1A</p></li></ol><p>4.\u4E3A\u4EC0\u4E48\u8981\u5F02\u6B65\uFF1F</p><h3 id="\u4EFB\u52A1" tabindex="-1">\u4EFB\u52A1 <a class="header-anchor" href="#\u4EFB\u52A1" aria-hidden="true">#</a></h3><ul><li>MacroTask\uFF08\u5B8F\u4EFB\u52A1\uFF09</li><li>MicroTask\uFF08\u5FAE\u4EFB\u52A1\uFF09</li></ul><h1 id="" tabindex="-1"><a class="header-anchor" href="#" aria-hidden="true">#</a></h1>',29),o=[t];function r(s,n,d,c,h,_){return l(),e("div",null,o)}var f=p(a,[["render",r]]);export{v as __pageData,f as default};
